# Endpoint da API para criação de posts
posts_endpoint = f"{wordpress_url}/wp/v2/posts"

# Função para criar um post


def criar_post(titulo):
    headers = {
        "Content-Type": "application/json"
    }
    auth = (username, password)
    data = {
        "title": titulo,
        "status": "publish"  # Publica o post imediatamente
    }

    response = requests.post(posts_endpoint, json=data,
                             headers=headers, auth=auth)

    if response.status_code == 201:
        print(f"Post criado com sucesso: {titulo}")
    else:
        print(f"Erro ao criar o post: {titulo}")
        print(f"Status Code: {
              response.status_code}, Resposta: {response.text}")


# Criar todos os posts
for titulo in titulos:
    criar_post(titulo)
